import React, { useEffect, useState } from "react";
import axios from "axios";
import "./JobList.css"; // Import the CSS file

const JobList = () => {
  const [jobs, setJobs] = useState([]); // State to store the fetched jobs
  const [loading, setLoading] = useState(true); // State to handle loading state
  const [error, setError] = useState(null); // State to handle any errors

  useEffect(() => {
    // Make the POST request to fetch the jobs data
    const fetchJobs = async () => {
      try {
        // Retrieve the token from localStorage
        const token = localStorage.getItem("token");

        // If the token exists, include it in the request header
        const response = await axios.post(
          "http://localhost:5000/api/jobs",
          {}, // Empty body if you don't need to send data
          {
            headers: {
              Authorization: `Bearer ${token}`, // Include token in Authorization header
            },
          }
        );

        setJobs(response.data); // Set the jobs data in state
        setLoading(false); // Set loading to false after data is fetched
      } catch (err) {
        setError("Error fetching jobs."); // Set error message in case of failure
        setLoading(false); // Set loading to false if there's an error
      }
    };

    fetchJobs(); // Call the fetchJobs function
  }, []); // Empty dependency array means this effect runs once when the component mounts

  if (loading) {
    return <p>Loading jobs...</p>; // Show loading message while the data is being fetched
  }

  if (error) {
    return <p>{error}</p>; // Show error message if there was an error
  }

  return (
    <div className="job-list">
      <h2 className="job-list-title">Job Listings</h2>
      <ul className="job-list-items">
        {jobs.length === 0 ? (
          <li className="job-list-empty">No jobs available</li>
        ) : (
          jobs.map((job) => (
            <li key={job._id} className="job-list-item">
              <h3 className="job-list-item-title">{job.title}</h3>
              <p className="job-list-item-company">{job.company}</p>
              <p className="job-list-item-location">{job.location}</p>
              <a href={`/jobs/${job._id}`} className="job-list-item-link">
                View Details
              </a>
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default JobList;
