import { createStore, applyMiddleware } from "redux";
import thunk from "redux-thunk"; // For handling async actions
import rootReducer from "./reducers"; // Combine your reducers here

const store = createStore(rootReducer, applyMiddleware(thunk)); // Configure store with middleware

export default store;
